mapper.select:
SELECT pq.*, (SELECT count(qa.id) FROM t_quotation_application qa WHERE qa.quotation_id=pq.id and qa.type=2) AS applicationCount FROM t_purchase_quotation pq
AND pq.end_date >= (select date_format(now(),'%Y-%m-%d')) AND pq.op_id=#{opId}
order by pq.update_date desc limit #{start},#{length}
